import React from 'react'
import s from './Dialogs.module.css'
import {NavLink} from 'react-router-dom'

const DialogItem=(props)=>
{
    const path=`/dialogs/${props.id}`
    return (
        <div className={s.dialog+' '+s.active}>
                    <NavLink to={path}>{props.name}</NavLink></div>
    )
}

const Message=(props)=>
{
return (<div className={s.message}>{props.message}</div>)
}

const dialogsData=[{name: 'Almaz', id: 1},{name: 'Vika', id: 2}]

const messagesData=[{id:1, message: 'Привет, как дела?'},{id:2,message: 'Привет, все хорошо, как твои?'}]

export default function Dialogs() {
    const objArr=[ <DialogItem name={dialogsData[0].name} id={dialogsData[0].id}/>,
    <DialogItem name={dialogsData[1].name} id={dialogsData[1].id}/>]
    const dialogsElement=dialogsData.map((el)=>(<DialogItem name={el.name} id={el.id}/>))
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
               {dialogsElement}
            </div>
            <div className={s.messages}>
                <Message message={messagesData[0].message}/>
                <Message message={messagesData[1].message}/>
            </div>
        </div>
    )
}
