const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS='SET-USERS'

let initialState = {
    users: [
        {id: 1, followed: false, fullName: 'Almaz', status: 'i am a boss', location: {city: 'Kazan', country: 'Russia'} },
        {id: 2, followed: true, fullName: 'Andrew', status: 'i am a boss too', location: {city: 'Kiev', country: 'Ukraine'} },
        {id: 3, followed: false, fullName: 'Dmitry', status: 'i am a boss too', location: {city: 'Minsk', country: 'Belarus'} }
    ]
};

const userReducer = (state = initialState, action) => {

    switch(action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map((i)=>{
                    if (i.id===action.userId)
                    {
                        return {...i,followed: true}
                    }
                    return i
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map((i)=>{
                    if (i.id===action.userId)
                    {
                        return {...i,followed: false}
                    }
                    return i
                })
            }
        case SET_USERS:
            return {
                ...state,
                users: [...state.users, ...action.users]
            }

        default:
            return state;
    }
}


export const followAC = (id) => ({type: FOLLOW, userID: id})
export const unfollowAC = (id) => ({type: UNFOLLOW, userID: id})
export const setUsersAC=(users)=>({type: SET_USERS, users: users})

export default userReducer;
