import React from 'react'
import {NavLink} from "react-router-dom";
import { Field, reduxForm } from 'redux-form'
import {AuthAPI} from '../../api/api'

const AvtorizationForm=(props)=>{
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field component={'input'} placeholder={'Введите логин'} name={'login'}/>
            </div>
            <div>
                <Field component={'input'} placeholder={'Введите пароль'} name={'password'}/>
            </div>
            <div>
                <Field component={'input'} type={'checkbox'} name={'rememberMe'}/> Запомнить меня
            </div>
            <div>
                <NavLink to={'/registration'}>Зарегистрироваться</NavLink>
            </div>
            <div>
                <button>Вход</button>
            </div>

        </form>)
}

const AdvtorizationReduxForm = reduxForm({form: 'login'})(AvtorizationForm)


let Avtorization=(props)=>{
    console.log(props)
    const onSubmit=(data)=>{
        AuthAPI.login(data.login,data.password);
        console.log(data)
    }
    return <>
        <h1>Вход</h1>
        <AdvtorizationReduxForm onSubmit={onSubmit}/>
    </>
}

let mapStateToProps=(state)=>{
    isAuth: state.auth.isAuth
}

export default connect(mapStateToProps)(Avtorization)
