import React from 'react';
import styles from './users.module.css';
import * as axios from 'axios'

let Users=(props)=>{

    if (props.users.length==0) {
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response=>{
            props.setUsers(response.data.items);
        })
    }
    return <div>
        {
        props.users.map((i)=><div key={i.id}>
                <span>
                    <div>
                        <img src={i.photoUrl} className={styles.userPhoto}/>
                    </div>
                    <div>
                        {i.followed ? <button onClick={()=>{props.unfollow(i.id)}}>Unfollow</button>  : <button onClick={()=>{props.follow(i.id)}}>Follow</button>}

                    </div>
                </span>
            <span>
                    <span>
                        <div>{i.name}</div>
                        <div>{i.status}</div>
                    </span>
                    <span>
                        <div>{"i.location.country"}</div>
                        <div>{"i.location.city"}</div>
                    </span>
                </span>
        </div>)
        }
    </div>

}

export default Users;
