import React, {Component} from 'react';
import s from './Profile.module.css';
import Interests from "./Interests";
import * as axios from "axios";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRedirect} from '../../hoc/withRedirect'
import {changeInterests,buttonIsClicked,createInterests,clearInterests} from "../../redux/profile-reducer";

let mapStateToProps=(state)=>{
    return {
        profilePage: state.profilePage,
    }
}



class Profile extends Component {
    componentDidMount() {
        console.log('Прорисовка profile')
        axios.get(`https://chat-server-project.herokuapp.com/api/messages?user_getting=4`,{withCredentials: true})
            .then(response=>{console.log(response)}).catch(()=>console.log('Запрос не пошел. В файле Profile.jsx'))
        }

     render() {
        return (
            <div>
                <div className={s.descriptionBlock}>
                    <div>Имя пользователя: {this.props.profilePage.name}</div>
                    <div>Рейтинг: {this.props.profilePage.rating}</div>
                    <Interests clearInterests={this.props.clearInterests} createInterests={this.props.createInterests} btn={this.props.profilePage.btn} buttonIsClicked={this.props.buttonIsClicked} interests={this.props.profilePage.interests} changeInterests={this.props.changeInterests}/>


                </div>
            </div>
        )
    }
}



export default compose(
    connect(mapStateToProps,{changeInterests,buttonIsClicked,createInterests,clearInterests}),
    withRedirect
)(Profile)
