let f1=(state)=>{
    return {
        dialogsPage: state.dialogsPage
    }
}

let f2=(dispatch)=>{
    return {
        updateNewMessageBody: (body)=>{
            dispatch(updateNewMessageBodyCreator(body));
        },
        sendMessage: ()=>{
            dispatch(sendMessageCreator());
        }
    }
}

const DialogsContainer=connect(f1,f2)(Dialogs)

export default DialogsContainer;
