const CHANGE_INTERESTS=CHANGE_INTERESTS;
let initialState = {
    name: 'Ослепительный бык',
    rating: 3,
    interests: {
        "sport": 0,
        "creation": 1,
        "music": 0,
        "literature": 0,
        "film": 0,
        "science": 0,
        "cars": 1,
        "technology": 0,
        "politics": 0,
        "travel": 1,
        "cooking": 1,
        "art": 0,
        "games": 0,
        "craft": 0,
        "parenting": 0
    }


};

const profileReducer = (state = initialState, action) => {

    switch(action.type) {
        case CHANGE_INTERESTS:
            return {
                ...state,
                interests: {...action.interests}
            }


        default:
            return state;
    }
}

export const changeInterests=(interests)=>({type:CHANGE_INTERESTS, interests })


export default profileReducer;
