import profileReducer from "./profile-reducer";
import dialogsReducer from "./dialogs-reducer";
import sidebarReducer from "./sidebar-reducer";

const ADD_POST='ADD-POST';// добавляет пост
const NEW_POST_TEXT='NEW-POST-TEXT'; // каждый символ в текстареа для поста добавляет в state
const NEW_MESSAGE_BODY='NEW-MESSAGE-BODY'; // каждый символ в текстареа для сообщения добавляет в state
const SEND_MESSAGE='SEND-MESSAGE'; // отправляет сообщение

let store ={
    _subscribe(observer) {
        console.log('Observers is null');
    },
    _state: {
        profilePage: {
            posts: [
                {id: 1, message: 'Hi, how are you?', likesCount: 12},
                {id: 2, message: 'It\'s my first post', likesCount: 11},
                {id: 3, message: 'Blabla', likesCount: 11},
                {id: 4, message: 'Dada', likesCount: 11}
            ],
            newPostText: 'it-kamasutra.com'
        },
        dialogsPage: {
            dialogs: [
                {id: 1, name: 'Dimych'},
                {id: 2, name: 'Andrew'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Sasha'},
                {id: 5, name: 'Viktor'},
                {id: 6, name: 'Valera'}
            ],
            messages: [
                {id: 1, message: 'Hi'},
                {id: 2, message: 'How is your it-kamasutra?'},
                {id: 3, message: 'Yo'},
                {id: 4, message: 'Yo'},
                {id: 5, message: 'Yo'}
            ],
            newMessageBody: ''
        },
        sidebar: {}
    },

    subscribe(observer) {
      this._subscribe=observer;
    },
    getState() {
        return this._state;
    },

    addPost() {
        let newPost = {
            id: 5,
            message: this._state.profilePage.newPostText,
            likesCount: 0
        };
        this._state.profilePage.posts.push(newPost);
        this._state.profilePage.newPostText = '';
        this._subscribe();
    },
    updateNewPostText(newText) {
        this._state.profilePage.newPostText = newText;
        this._subscribe();
    },
    dispatch(action) {
        profileReducer(this._state.profilePage, action);
        dialogsReducer(this._state.dialogsPage,action);
        sidebarReducer(this._state.sidebar,action);

        this._subscribe()
    }
}

export const addPostActionCreator=()=>({type: ADD_POST});
export const onPostChangeActionCreator=(text)=>({type: NEW_POST_TEXT, newText: text });
export const updateNewMessageBodyCreator=(body)=>({type: NEW_MESSAGE_BODY, body: body});
export const sendMessageCreator=()=>({type: SEND_MESSAGE});


export default store;
