import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import { Field, reduxForm } from 'redux-form';
import {Route} from "react-router-dom";
import {withRouter} from "react-router-dom";

const AddMessageForm=(props)=>{
    return (
        <form onSubmit={props.handleSubmit}>
            <div><Field component={'textarea'} name={"message"} placeholder='Enter your message'/></div>
            <div><button>Send</button></div>
        </form>
    )
}

let AddMessageFormRedux=reduxForm({form: 'addMessage'})(AddMessageForm)


class Dialogs extends React.Component {
    state={
        id: null
    }
    render() {
        let state = this.props.dialogsPage;

        let dialogsElements = state.dialogs.map(d => <DialogItem name={d.name} key={d.id} id={d.id}/>);


        let onSubmit = (data) => {
            this.props.sendMessage(data.message);
        }
        return (
            <div className={s.dialogs}>
                <div className={s.dialogsItems}>
                    {dialogsElements}
                </div>
                <div className={s.messages}>
                    <div>
                        <Route path='/dialogs/:userId?'
                               render={() => <Message {...this.props} messages={state.messages}/>}/>
                    </div>
                    <AddMessageFormRedux onSubmit={onSubmit}/>
                </div>
            </div>
        )
    }
}


export default withRouter(Dialogs);
