import React from 'react'
import s from './Dialogs.module.css'
import DialogItem from './DialogItem/DialogItem'
import Messages from './Messages/Messages'





export default function Dialogs(props) {

    let dialogsElement=props.state.dialogsData.map(el=>(<DialogItem name={el.name} id={el.id} img={el.img}/>))
    let messagesElement=props.state.messagesData.map(message=>(<Messages message={message.message}/>))

    let linkTextarea=React.createRef();

    let linkLabel=React.createRef();

    let addPost=()=>{
        linkLabel.current.value=linkTextarea.current.value
    }

    letchangeText=(e)=> {
        e.target.value=e.target.value;
    }


    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
               {dialogsElement}
            </div>
            <div className={s.messages}>
              {messagesElement}
              <label onChange={changeText} ref={linkLabel}>Првиет</label>
              <div>
                <div>
                    <textarea ref={linkTextarea}></textarea>
                </div>
                <div>
                    <button onClick={addPost}>Добавить</button>
                </div>
            </div>
            </div>

        </div>
    )
}
