const NEW_MESSAGE_BODY='NEW-MESSAGE-BODY'; // каждый символ в текстареа для сообщения добавляет в state
const SEND_MESSAGE='SEND-MESSAGE'; // отправляет сообщение

export const updateNewMessageBodyCreator=(body)=>({type: NEW_MESSAGE_BODY, body: body});
export const sendMessageCreator=()=>({type: SEND_MESSAGE});

const dialogsReducer=(state, action)=>{
    switch (action.type) {
        case NEW_MESSAGE_BODY:
            state.newMessageBody = action.body;
            return state;
        case SEND_MESSAGE:
            let body = state.newMessageBody;
            state.messages.push({id: 6, message: body})
            state.newMessageBody = '';
            return state;
        default:
            return state;
    }
}
export default dialogsReducer;
