const UPDATE_NEW_MESSAGE_BODY = 'UPDATE_NEW_MESSAGE_BODY';
const SEND_MESSAGE = 'SEND_MESSAGE';
const FIND_FRIEND='FIND_FRIEND'

let initialState = {
    dialogs: [
        // {id: 1, name: 'Неописуемый Олень', messages: [{meOryou:true, message: 'Привет как дела?'}]},
        // {id: 2, name: 'Немилосердный Баран', messages: []},
        // {id: 3, name: 'Неоценимый Жираф', messages: []},
        // {id: 4, name: 'Немаловажный Лось', messages: []},
        // {id: 5, name: 'Непереносимый Тюлень', messages: []},
        // {id: 6, name: 'Неоспоримый Бегемот', messages: []}
    ],
    messages: [
        {id: 1, message: 'Привет'},
        {id: 2, message: 'Как твои дела?'},
        {id: 3, message: 'Привет. Все хорошо. Как твои?'},
        {id: 4, message: 'Нормально, какую музыку предпочитаешь'},
        {id: 5, message: 'Любишь кино?'}
    ]
};

const dialogsReducer = (state = initialState, action) => {
   switch (action.type) {
       case SEND_MESSAGE:
           let body = action.data
           let ind;
           let newObj=state.dialogs.find((i,index)=>{
               ind=index
               return i.id==action.id})
           let newArr=[...state.dialogs]
           newArr[ind].messages.push({meOryou: true, message: body})
           return {
               dialogs: [...newArr],
               messages: [...state.messages, {id: 6, message: body}]
           }
       case FIND_FRIEND:
           return {
               ...state,
               dialogs: [...state.dialogs, action.dataFriend]
           }
       default:
           return state;
   }
}

export const sendMessageCreator = (data,id) => ({type: SEND_MESSAGE, data:data, id})

export default dialogsReducer;
