import {rendereEntireTree} from './../render'
let state= {
    profilePage: {
        postData: [{id: 1, message:'Hello, how are you?',countLike: '12'},
              {id: 2, message:'Hello, i am fine thank you',countLike: '19'}],
        newPostText: 'Алмаз, ты чего?'
    },
    dialogsPage: {
        dialogsData: [{name: 'Almaz', id: 1, img: 'https://i.pinimg.com/originals/6a/2f/84/6a2f842a48ac102545898fd4db375feb.jpg'},
                      {name: 'Alexey', id: 2, img: 'https://i.pinimg.com/736x/dc/f6/b3/dcf6b3dde40619bf4c0b6dfbf2b9960a.jpg' },
                      {name: 'Vika', id: 3, img: 'https://i.pinimg.com/736x/4f/b4/56/4fb4560f68e485385b5b92915878016d--zuko-the-last-airbender.jpg'},
                      {name: 'Tanya', id: 4, img: 'https://sun9-43.userapi.com/c846018/v846018618/1e12f5/daswmNJMvrQ.jpg'}],

         messagesData: [{id:1, message: 'Привет, как дела?'},
                  {id:2,message: 'Привет, все хорошо, как твои?'}]
    },
    sidebar: [{item: 'Profile', path: '/profile'},
              {item: 'Messages', path: '/dialogs'},
              {item: 'News', path: '/dgs'},
              {item: 'Music', path: '/s4'},
              {item: 'Settings', path: '/fe'}]

};

export let addPost=(msg)=>
{
    let newPost={
        id: 3, message: msg, countLike:0
    }
    state.profilePage.postData.push(newPost)
    rendereEntireTree(state);
}

export let updateNewPostText=(msg)=>
{
    debugger;
    state.profilePage.newPostText='msg'
    rendereEntireTree(state);
}



export default state
