import React from 'react'
import s from './Dialogs.module.css'
import {NavLink} from 'react-router-dom'

const DialogItem=(props)=>
{
    let path=`/dialogs/${props.id}`
    return (
        <div className={s.dialog+' '+s.active}>
                    <NavLink to={path}>{props.name}</NavLink></div>
    )
}

const Message=(props)=>
{
return (<div className={s.message}>{props.message}</div>)
}

let dialogsData=[{name: 'Almaz', id: 1},{name: 'Vika', id: 2}]

let messagesData=[{id:1, message: 'Привет, как дела?'},{id:2,message: 'Привет, все хорошо, как твои?'}]

export default function Dialogs() {

    let dialogsElement=dialogsData.map(el=>(<DialogItem name={el.name} id={el.id}/>))
    let messagesElement=messagesData.map(message=>(<Message message={message.message}/>))
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
               {dialogsElement}
            </div>
            <div className={s.messages}>
              {messagesElement}
                <Message message={messagesData[1].message}/>
            </div>
        </div>
    )
}
