import React from 'react';
import s from './Profile.module.css';
import { Field,reduxForm } from 'redux-form';


const Interests = (props) => {

    let translateInterests={
        "sport": 'спорт',
        "creation": 'творчество',
        "music": 'музыка',
        "literature": 'литература',
        "film": 'кино',
        "science": 'наука',
        "cars": 'автомобили',
        "technology": 'техника',
        "politics": 'политика',
        "travel": 'путешествие',
        "cooking": 'кулинария',
        "art": 'искусство',
        "games": 'игры',
        "craft": 'ремесло',
        "parenting": 'материнство/отцовство'
    }

    let arr=[];
    for (let i in props.interests){
        if (props.interests[i]==1)
        {
            for (let u in translateInterests)
                if (i==u) arr.push(translateInterests[u])
        }
    }
    let showInterests=arr.join(', ')

    let changeItemsInterests=()=>{
        let interests = {
            "sport": 0,
            "creation": 1,
            "music": 0,
            "literature": 0,
            "film": 0,
            "science": 0,
            "cars": 1,
            "technology": 0,
            "politics": 0,
            "travel": 1,
            "cooking": 0,
            "art": 0,
            "games": 1,
            "craft": 1,
            "parenting": 0
        }
        props.changeInterests(interests)
    }

    return (<>
                <div>Интересы: {showInterests} </div>
            <button onClick={changeItemsInterests}>Изменить интересы</button>
            <ChangeInterestsFormRedux interests={props.interests} translateInterests={translateInterests}/>


        </>
    )
}
const ChangeInterestsForm=(props)=>{

    let arr=[]

        for (let i in props.interests){
            if (props.interests[i]==1)
            {
                for (let u in translateInterests)
                {
                    if (i==u) {
                        arr.push(<div><Field component={'input'} type="checkbox" name={i}/>
                        <label htmlFor={i}>translateInterests[u]</label> </div>)
                    }
                }
            }
        }



    return (<form>

        <Field component={'input'} type="checkbox" name='sport'/>
        <label htmlFor="sport">Спорт</label>
    </form>)
}

const ChangeInterestsFormRedux=reduxForm({form: 'interests'})(ChangeInterestsForm)



export default Interests;
