import React from 'react'
import {NavLink} from "react-router-dom";
import { Field, reduxForm } from 'redux-form'
import {AuthAPI} from '../../api/api'
import {connect} from 'react-redux'
import {setUserData} from '../../redux/auth-reducer'
import {Redirect} from "react-router-dom";
import {required} from '../../validator/validator'


const AvtorizationForm=(props)=>{
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field validate={[required]} component={'input'} placeholder={'Введите логин'} name={'login'}/>
            </div>
            <div>
                <Field  type={'password'} component={'input'} placeholder={'Введите пароль'} name={'password'}/>
            </div>
            <div>
                <Field component={'input'} type={'checkbox'} name={'rememberMe'}/> Запомнить меня
            </div>
            <div>
                <NavLink to={'/registration'}>Зарегистрироваться</NavLink>
            </div>
            <div>
                <button>Вход</button>
            </div>

        </form>)
}

const AdvtorizationReduxForm = reduxForm({form: 'login'})(AvtorizationForm)


let Avtorization=(props)=>{
    const onSubmit=(data)=>{
        AuthAPI.login(data.login,data.password);
        //В случае успешной авторизации
        const response={ login: "Almaz", id: "12"}
        props.setUserData(response.id, data.login)// Здесь в дальнейшем должны передаваться id, login
    }
    return <>
        <h1>Вход</h1>
            {props.isAuth?<Redirect to={'/profile'}/>:<AdvtorizationReduxForm onSubmit={onSubmit}/>}
    </>
}

let mapStateToProps=(state)=>({
    isAuth: state.auth.isAuth
})



export default connect(mapStateToProps, {setUserData})(Avtorization)
