import React from 'react';
import styles from './users.module.css';

let Users=(props)=>{

    props.setUsers([
        {id: 1, photoUrl: 'https://vokrug.tv/pic/news/f/d/a/f/fdafa4953b333881d795aeac1b0e392a.jpg', followed: false, fullName: 'Almaz', status: 'i am a boss', location: {city: 'Kazan', country: 'Russia'} },
        {id: 2, photoUrl: 'https://vokrug.tv/pic/news/f/d/a/f/fdafa4953b333881d795aeac1b0e392a.jpg', followed: true, fullName: 'Andrew', status: 'i am a boss too', location: {city: 'Kiev', country: 'Ukraine'} },
        {id: 3, photoUrl: 'https://vokrug.tv/pic/news/f/d/a/f/fdafa4953b333881d795aeac1b0e392a.jpg', followed: false, fullName: 'Dmitry', status: 'i am a boss too', location: {city: 'Minsk', country: 'Belarus'} }
    ]);
    return <div>
        {
        props.users.map((i)=><div key={i.id}>
                <span>
                    <div>
                        <img src={i.photoUrl} className={styles.userPhoto}/>
                    </div>
                    <div>
                        {i.followed ? <button onClick={()=>{props.unfollow(i.id)}}>Unfollow</button>  : <button onClick={()=>{props.follow(i.id)}}>Follow</button>}

                    </div>
                </span>
            <span>
                    <span>
                        <div>{i.fullName}</div>
                        <div>{i.status}</div>
                    </span>
                    <span>
                        <div>{i.location.country}</div>
                        <div>{i.location.city}</div>
                    </span>
                </span>
        </div>)
        }
    </div>

}

export default Users;
