import React from 'react';
import s from './Profile.module.css';

const Interests = (props) => {

    let translateInterests={
        "sport": 'спорт',
        "creation": 'творчество',
        "music": 'музыка',
        "literature": 'литература',
        "film": 'кино',
        "science": 'наука',
        "cars": 'автомобили',
        "technology": 'техника',
        "politics": 'политика',
        "travel": 'путешествие',
        "cooking": 'кулинария',
        "art": 'искусство',
        "games": 'игры',
        "craft": 'ремесло',
        "parenting": 'материнство/отцовство'

    }

    let arr=[];
    for (let i in props.interests){
        if (props.interests[i]==1)
        {
            for (let u in translateInterests)
                if (i==u) arr.push(translateInterests[u])
        }
    }
    let showInterests=arr.join(', ')

    return (<>
                <div>Интересы: {showInterests} </div>
                <button>Изменить</button>
        </>
    )
}


export default Interests;
