const ADD_POST='ADD-POST';// добавляет пост
const NEW_POST_TEXT='NEW-POST-TEXT'; // каждый символ в текстареа для поста добавляет в state

export const addPostActionCreator=()=>({type: ADD_POST});
export const onPostChangeActionCreator=(text)=>({type: NEW_POST_TEXT, newText: text });

const profileReducer=(state, action)=>{
    switch (action.type)
    {
        case ADD_POST:
            let newPost = {
                id: 5,
                message: state.newPostText,
                likesCount: 0
            };
            state.posts.push(newPost);
            state.newPostText = '';
            return state;
        case NEW_POST_TEXT:
            state.newPostText = action.newText;
            return state;
        default:
            return state;

    }
}

export default profileReducer;
